import React from "react";
import BookShelf from "./BookShelf";

const BookShelves = ({ books, updateBookShelf }) => {
    const currentlyReading = books.filter((book) => book.bookShelf === "currentlyReading");
    const wantToRead = books.filter((book) => book.bookShelf === "wantToRead");
    const read = books.filter((book) => book.bookShelf === "read");

    return (
        <div>
            <BookShelf bookShelfTitle="Currently Reading" books={currentlyReading} books={books} updateBookShelf/>
            <BookShelf bookShelfTitle="Want To Read" books={wantToRead} books={books} changeBookShelf/>
            <BookShelf bookShelfTitle="Read" books={read} books={books} changeBookShelf/>
        </div>
    )
}

export default BookShelves;
