import axios from 'axios';
import React, { useState } from 'react'
import * as BooksAPI from '../BooksAPI';
import { Link } from 'react-router-dom';




const BookSearchPage = () => {   
    const [
        bookearchTerm, 
        setSearchTerm, 
        showBookSearchPage, 
        setBookSearchPage,
        resetBookSearchPage,
        reset
    ] = useState('');



    const onInputChange = (e) => {
        setSearchTerm(e.target.value);
    }
