import axios from 'axios';
import React, { useState, useEffect } from 'react';
import * as BooksAPI from '../BooksAPI';


const BookSearchPage = () => {   
  const [searchTerm, setSearchTerm, setBooks] = useState("");
  const [bookSearchResults, setBookSearchResults] = useState([]);
  const [showSearchPage] = useState(false);

  useEffect(() => {
    BooksAPI.getAll()
      .then(data => setBooks(data))
    console.log(data);
  }, [])


  return (
    <div className="app">
      <div className="serach-books">
        <div className="search-books-bar">
        <button className="close-search" onClick={() => showSearchPage(false)}>Close</button>
          <div className="search-books-input-wrapper">
            <input type="text" placeholder="Search by title or author" value={searchTerm} onChange={e => setSearchTerm(e.target.value)} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default BookSearchPage;
