import React from 'react';

const BookSearchForm = ({onSubmitHandler, searchTerm, onInputChange, errorMessage}) => {
    return (
        <form onSubmit={onSubmitHandler}>
            <input type="search" placeholder="Search by title or author"
                value={searchTerm}
                onChange={onInputChange}
                required
            />
            <Button type="submit">Search</Button>
            {errorMessage && (
                <ErrorTextMessage>Some error occured while fetching books API</ErrorTextMessage>
            )}
        </form>
    )
}

export default BookSearchForm;
