import React from "react";
import Book from "./Book";

const BookSearchResults = () => {
    const [books, setBooks, changeBookShelf] = useState([])

    const updateBookShelf = (book, newBookShelf) => {
      const updatedBooks = books.map(b => {
        if (b.id === book.id) {
          book.shelf = newBookShelf;
          return book;
        }
        return b;
      });
      setBooks(updatedBooks)
      BooksAPI.update(book, newBookShelf).then(data => console.log(data))
    }

    return (
        <div className="search-books-results">
              <ol className="books-grid">
                <Book key={book.id} book={book} bookShelf={book.self}
                />
              </ol>
            </div>   
    );
}

export default BookSearchResults
