import axios from 'axios';
//import * as BooksAPI from '../BooksAPI';


const BookSearchPage = (props) => {   
  const [searchTerm, setSearchTerm] = useState("");
  const [bookSearchResults, setBookSearchResults] = useState({});
  //const [showSearchPage] = useState(false);
  const [errorMessage, setErrorMessage] = useState(false);

  useEffect(() => {
    const bookSearch = async () => {
      const { data } = await axios.get("https://reactnd-books-api.udacity.com", {
        params: {
          action: "query",
          list: "bookSearch",
          origin: "*",
          format: "json",
          srsearch: "searchTerm",
        },
      });
      setBookSearchResults(data)
      console.log(data)
    }
    if (searchTerm) {
      bookSearch();
    }
  }, [searchTerm]);


  return (
    <div className="app">
      <div className="serach-books">
        <div className="search-books-bar">
        <button className="close-search" onClick={() => showSearchPage(false)}>Close</button>
          <div className="search-books-input-wrapper">
            <input type="text" placeholder="Search by title or author" value={searchTerm} onChange={e => setSearchTerm(e.target.value)} autoFocus/>
          </div>
        </div>
      </div>
    </div>
  );
}

export default BookSearchPage;
